    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Book Details{% endblock %}

{% block body %}
<div class=\"inner-container grid-container\">
    <div class=\"sidebar\">
        <ul>
            <li><a href=\"{{ path('library_home') }}\">Library</a></li>
            <li><a href=\"{{ path('library_view_all') }}\">View all books</a></li>
            <li><a href=\"{{ path('library_create') }}\">Add book to library</a></li>
        </ul>
    </div>
    <div class=\"content\">
        <h1>{{ book.title }}</h1>
        <div class=\"book-details\">
            <img src=\"{{ asset('img/' ~ book.image) }}\" alt=\"{{ book.title }}\">
            <h3>{{ book.title }}</h3>
            <p>Author: {{ book.author }}</p>
            <p>ISBN: {{ book.isbn }}</p>
        </div>
        <div class=\"book-actions\">
            <a href=\"{{ path('library_edit', { id: book.id }) }}\" class=\"btn\">Edit</a>
            <a href=\"{{ path('library_delete', { id: book.id }) }}\" class=\"btn\">Delete</a>
        </div>
    </div>
</div>
{% endblock %}
", "library/book.html.twig", "/Users/hannagawlitza/dbwebb-kurser/mvc/me/report/templates/library/book.html.twig");
